    color: black;
}

learningObject.script:
//IMPORTANT DO NOT DELETE, CHANGE OR SHARE THIS CUSTOM SCRIPT
//WITHOUT WRITTEN PERMISSION
//Copyright Ron MItchell mitchellmedia.co.uk
var x_PL = x_pageLoaded,
    x_pageLoaded = function() {
        x_PL();
        addFL();
    },
    addFL = function() {
        $(  $("#pageContents").find('img') ).each(function( index ) {
            var imgpath=$( this ).prop('src');
            $( this ).wrap('<a data-featherlight="image" href="'+imgpath+'">');
        });
        $.featherlight.prototype.afterContent = function () {
            var caption = this.$currentTarget.find('img').attr('alt');
            this.$instance.find('.caption').remove();
            $('<div class="caption">').text(caption).appendTo(this.$instance.find('.featherlight-content'));
        }
        $('.listItem').click(function() {
            setTimeout(function(){
                addFL();
            }, 500);
        });
    }
//END of CUSTOM SCRIPT

$( "#x_footerProgress p" ).replaceWith( "<p>© MTCS (UK) Ltd 2020</p>" );
